// src/pages/CadastroMedico.js
import React, { useState } from 'react';
import '../styles/CadastroMedicoAdmin.css'; // Arquivo CSS específico para estilização
import { useNavigate } from 'react-router-dom';

function CadastroMedico() {
    const navigate = useNavigate();

    // Estados para os campos do formulário
    const [nome, setNome] = useState('');
    const [crm, setCrm] = useState('');
    const [especialidade, setEspecialidade] = useState('');
    const [telefone, setTelefone] = useState('');
    const [status, setStatus] = useState(true);

    // Função de manipulação de envio
    const handleSubmit = (e) => {
        e.preventDefault();

        // Validações básicas e criação do objeto médico
        const medico = { nome, crm, especialidade, telefone, status };
        
        console.log("Médico cadastrado:", medico);
        
        // Após o cadastro, redireciona para a dashboard ou lista de médicos
        navigate('/admin/dashboard');
    };

    return (
        <div className="container mt-5">
            <h2>Cadastro de Médico</h2>
            <form onSubmit={handleSubmit} className="form-cadastro-medico">
                <div className="form-group mb-3">
                    <label>Nome Completo</label>
                    <input 
                        type="text" 
                        className="form-control" 
                        value={nome} 
                        onChange={(e) => setNome(e.target.value)} 
                        required 
                    />
                </div>

                <div className="form-group mb-3">
                    <label>CRM</label>
                    <input 
                        type="text" 
                        className="form-control" 
                        value={crm} 
                        onChange={(e) => setCrm(e.target.value)} 
                        required 
                    />
                </div>

                <div className="form-group mb-3">
                    <label>Especialidade</label>
                    <select 
                        className="form-control" 
                        value={especialidade} 
                        onChange={(e) => setEspecialidade(e.target.value)} 
                        required
                    >
                        <option value="">Selecione uma especialidade</option>
                        <option value="Clínico Geral">Clínico Geral</option>
                        <option value="Cardiologia">Cardiologia</option>
                        <option value="Dermatologia">Dermatologia</option>
                        <option value="Pediatria">Pediatria</option>
                        <option value="Ortopedia">Ortopedia</option>
                    </select>
                </div>

                <div className="form-group mb-3">
                    <label>Telefone</label>
                    <input 
                        type="tel" 
                        className="form-control" 
                        value={telefone} 
                        onChange={(e) => setTelefone(e.target.value)} 
                        required 
                    />
                </div>

                <div className="form-group form-check mb-3">
                    <input 
                        type="checkbox" 
                        className="form-check-input" 
                        checked={status} 
                        onChange={(e) => setStatus(e.target.checked)} 
                    />
                    <label className="form-check-label">Status Ativo</label>
                </div>

                <button type="submit" className="btn btn-primary">Cadastrar Médico</button>
                <button 
                    type="button" 
                    className="btn btn-secondary ms-2" 
                    onClick={() => navigate('/admin/dashboard')}
                >
                    Cancelar
                </button>
            </form>
        </div>
    );
}

export default CadastroMedico;
