// src/pages/LoginMedico.js

import React, { useState } from 'react';
import { Form, Button, Alert } from 'react-bootstrap';
import { useNavigate } from 'react-router-dom';
import '../styles/CadastroMedico.css';

function CadastroMedico() {
  const [nome, setNome] = useState('');
  const [crm, setCrm] = useState('');
  const [especialidade, setEspecialidade] = useState('');
  const [telefone, setTelefone] = useState('');
  const [errorMessage, setErrorMessage] = useState('');

  const navigate = useNavigate();

  const handleSubmit = (e) => {
    e.preventDefault();

    // Validação para campos vazios
    if (!nome || !crm || !especialidade || !telefone) {
      setErrorMessage("Por favor, preencha todos os campos.");
      return;
    }

    // Salvar dados do médico no localStorage para serem exibidos na dashboard
    const medicoData = {
      nome,
      crm,
      especialidade,
      telefone,
    };
    localStorage.setItem('medico', JSON.stringify(medicoData));

    // Redirecionar para a Dashboard do Médico
    navigate('/dashboard-medico');
  };

  return (
    <div className="cadastro-medico-form">
      <h3>Cadastro de Médico</h3>

      {errorMessage && <Alert variant="danger">{errorMessage}</Alert>}

      <Form onSubmit={handleSubmit}>
        <Form.Group controlId="formNome">
          <Form.Label>Nome</Form.Label>
          <Form.Control 
            type="text" 
            placeholder="Nome do Médico" 
            value={nome}
            onChange={(e) => setNome(e.target.value)} 
          />
        </Form.Group>

        <Form.Group controlId="formCrm">
          <Form.Label>CRM</Form.Label>
          <Form.Control 
            type="text" 
            placeholder="CRM do Médico" 
            value={crm}
            onChange={(e) => setCrm(e.target.value)} 
          />
        </Form.Group>

        <Form.Group controlId="formEspecialidade">
          <Form.Label>Especialidade</Form.Label>
          <Form.Control 
            type="text" 
            placeholder="Especialidade do Médico" 
            value={especialidade}
            onChange={(e) => setEspecialidade(e.target.value)} 
          />
        </Form.Group>

        <Form.Group controlId="formTelefone">
          <Form.Label>Telefone</Form.Label>
          <Form.Control 
            type="text" 
            placeholder="Telefone do Médico" 
            value={telefone}
            onChange={(e) => setTelefone(e.target.value)} 
          />
        </Form.Group>

        <Button variant="primary" type="submit">
          Cadastrar Médico
        </Button>
      </Form>
    </div>
  );
}

export default CadastroMedico;
