// src/pages/ConsultasAgendadasMedico.js

import React, { useEffect, useState } from 'react';
import { Table } from 'react-bootstrap';
import axios from 'axios';

function ConsultasAgendadasMedico() {
    const [consultas, setConsultas] = useState([]);

    useEffect(() => {
        // Função para buscar as consultas agendadas do médico
        const fetchConsultas = async () => {
            try {
                const response = await axios.get('/api/consultas/agendadas'); // Ajuste a URL conforme necessário
                setConsultas(response.data);
            } catch (error) {
                console.error('Erro ao buscar as consultas agendadas:', error);
            }
        };

        fetchConsultas();
    }, []);

    return (
        <div>
            <h2>Consultas Agendadas</h2>
            <Table striped bordered hover>
                <thead>
                    <tr>
                        <th>ID</th>
                        <th>Paciente</th>
                        <th>Data e Hora</th>
                        <th>Motivo</th>
                        <th>Status</th>
                    </tr>
                </thead>
                <tbody>
                    {consultas.map(consulta => (
                        <tr key={consulta.id}>
                            <td>{consulta.id}</td>
                            <td>{consulta.paciente.nome}</td>
                            <td>{new Date(consulta.dataHora).toLocaleString()}</td>
                            <td>{consulta.motivo}</td>
                            <td>{consulta.status}</td>
                        </tr>
                    ))}
                </tbody>
            </Table>
        </div>
    );
}

export default ConsultasAgendadasMedico;
