// src/pages/CadastroMedico.js

import React, { useState } from 'react';
import { Container, Button, Form, Alert } from 'react-bootstrap';
import { useNavigate } from 'react-router-dom';
import '../styles/LoginMedico.css';

const CadastroMedico = () => {
  const [medicoData, setMedicoData] = useState({
    nome: '',
    crm: '',
    especialidade: '',
    telefone: '',
  });

  const [errorMessage, setErrorMessage] = useState('');
  const navigate = useNavigate();

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setMedicoData({ ...medicoData, [name]: value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    if (!medicoData.nome || !medicoData.crm || !medicoData.especialidade || !medicoData.telefone) {
      setErrorMessage("Por favor, preencha todos os campos.");
      return;
    }

    localStorage.setItem('medico', JSON.stringify(medicoData));
    navigate('/DashboardMedico');
  };

  return (
    <Container className="cadastro-medico-container">
      <h1 className="title">Cadastrar Médico</h1>
      {errorMessage && <Alert variant="danger">{errorMessage}</Alert>}
      <Form onSubmit={handleSubmit} className="cadastro-form">
        <Form.Group controlId="formNome">
          <Form.Control
            type="text"
            name="nome"
            placeholder="Nome do Médico"
            value={medicoData.nome}
            onChange={handleInputChange}
            required
          />
        </Form.Group>
        <Form.Group controlId="formCrm">
          <Form.Control
            type="text"
            name="crm"
            placeholder="CRM do Médico"
            value={medicoData.crm}
            onChange={handleInputChange}
            required
          />
        </Form.Group>
        <Form.Group controlId="formEspecialidade">
          <Form.Control
            type="text"
            name="especialidade"
            placeholder="Especialidade do Médico"
            value={medicoData.especialidade}
            onChange={handleInputChange}
            required
          />
        </Form.Group>
        <Form.Group controlId="formTelefone">
          <Form.Control
            type="tel"
            name="telefone"
            placeholder="Telefone do Médico"
            value={medicoData.telefone}
            onChange={handleInputChange}
            required
          />
        </Form.Group>
        <Button variant="primary" type="submit" className="submit-btn">
          Cadastrar Médico
        </Button>
      </Form>
    </Container>
  );
};

export default CadastroMedico;
