// src/pages/MarcarConsultaAdmin.js
import React, { useState, useEffect } from 'react';
import '../styles/MarcarConsultaAdmin.css'; 
import { useNavigate } from 'react-router-dom';

function MarcarConsultaAdmin() {
    const navigate = useNavigate();

    const [pacientes, setPacientes] = useState([]);
    const [medicos, setMedicos] = useState([]);
    const [pacienteId, setPacienteId] = useState('');
    const [medicoId, setMedicoId] = useState('');
    const [dataConsulta, setDataConsulta] = useState('');
    const [horaConsulta, setHoraConsulta] = useState('');
    
    useEffect(() => {
        
        setPacientes([
            { id: 1, nome: 'João Silva' },
            { id: 2, nome: 'Maria Oliveira' },
            { id: 3, nome: 'Pedro Santos' }
        ]);

        setMedicos([
            { id: 1, nome: 'Dr. Ana Costa' },
            { id: 2, nome: 'Dr. Roberto Lima' },
            { id: 3, nome: 'Dr. Júlia Mendes' }
        ]);
    }, []);

    const handleSubmit = (e) => {
        e.preventDefault();
        const consulta = { pacienteId, medicoId, dataConsulta, horaConsulta };
        
        console.log("Consulta marcada:", consulta);
        navigate('/admin/dashboard');
    };

    return (
        <div className="marcar-consulta-container">
            <h2>Marcar Consulta do Paciente</h2>
            <form onSubmit={handleSubmit} className="form-marcar-consulta">
                <div className="form-group">
                    <label>Selecione o Paciente</label>
                    <select 
                        className="form-control" 
                        value={pacienteId} 
                        onChange={(e) => setPacienteId(e.target.value)} 
                        required
                    >
                        <option value="">Selecione um paciente</option>
                        {pacientes.map(paciente => (
                            <option key={paciente.id} value={paciente.id}>
                                {paciente.nome}
                            </option>
                        ))}
                    </select>
                </div>

                <div className="form-group">
                    <label>Selecione o Médico</label>
                    <select 
                        className="form-control" 
                        value={medicoId} 
                        onChange={(e) => setMedicoId(e.target.value)} 
                        required
                    >
                        <option value="">Selecione um médico</option>
                        {medicos.map(medico => (
                            <option key={medico.id} value={medico.id}>
                                {medico.nome}
                            </option>
                        ))}
                    </select>
                </div>

                <div className="form-group">
                    <label>Data da Consulta</label>
                    <input 
                        type="date" 
                        className="form-control" 
                        value={dataConsulta} 
                        onChange={(e) => setDataConsulta(e.target.value)} 
                        required 
                    />
                </div>

                <div className="form-group">
                    <label>Hora da Consulta</label>
                    <input 
                        type="time" 
                        className="form-control" 
                        value={horaConsulta} 
                        onChange={(e) => setHoraConsulta(e.target.value)} 
                        required 
                    />
                </div>

                <div className="button-group">
                    <button type="submit" className="btn btn-primary">Marcar Consulta</button>
                    <button 
                        type="button" 
                        className="btn btn-outline-secondary" 
                        onClick={() => navigate('/admin/dashboard')}
                    >
                        Cancelar
                    </button>
                </div>
            </form>
        </div>
    );
}

export default MarcarConsultaAdmin;
