// src/pages/DashboardPaciente.js

import React, { useEffect, useState } from 'react';
import { Container, Row, Col, ListGroup } from 'react-bootstrap';
import MarcarConsulta from './MarcarConsulta';
import VerExames from './VerExames';
import HistoricoConsultas from './HistoricoConsultas';
import '../styles/DashboardPaciente.css';

function DashboardPaciente() {
  const [currentSection, setCurrentSection] = useState('home');
  const [nomePaciente, setNomePaciente] = useState('');
  const [consultas, setConsultas] = useState([]);

  useEffect(() => {
    const pacienteData = JSON.parse(localStorage.getItem('paciente'));
    if (pacienteData) {
      setNomePaciente(pacienteData.name);
    }

    // Carregar consultas do localStorage
    const savedConsultas = JSON.parse(localStorage.getItem('consultas')) || [];
    setConsultas(savedConsultas);
  }, []);

  const handleAddConsulta = (novaConsulta) => {
    const updatedConsultas = [...consultas, novaConsulta];
    setConsultas(updatedConsultas);
    localStorage.setItem('consultas', JSON.stringify(updatedConsultas));
  };

  const renderContent = () => {
    switch (currentSection) {
      case 'marcar-consulta':
        return <MarcarConsulta onAddConsulta={handleAddConsulta} />;
      case 'ver-exames':
        return <VerExames />;
      case 'historico-consultas':
        return <HistoricoConsultas consultas={consultas} />;
      case 'perfil':
        return <div>Conteúdo para Perfil</div>;
      default:
        return (
          <div>
            <h2>Bem-vindo, {nomePaciente}!</h2>
            <p>Escolha uma opção no menu para começar.</p>
          </div>
        );
    }
  };

  return (
    <Container fluid className="dashboard-paciente vh-100">
      <Row className="h-100">
        <Col md={3} className="sidebar">
          <h4>Menu do Paciente</h4>
          <ListGroup variant="flush">
            <ListGroup.Item action onClick={() => setCurrentSection('marcar-consulta')}>
              Marcar Consulta
            </ListGroup.Item>
            <ListGroup.Item action onClick={() => setCurrentSection('ver-exames')}>
              Ver Exames
            </ListGroup.Item>
            <ListGroup.Item action onClick={() => setCurrentSection('historico-consultas')}>
              Histórico de Consultas
            </ListGroup.Item>
            <ListGroup.Item action onClick={() => setCurrentSection('perfil')}>
              Perfil
            </ListGroup.Item>
          </ListGroup>
        </Col>
        <Col md={9} className="content d-flex flex-column h-100">
          {renderContent()}
        </Col>
      </Row>
    </Container>
  );
}

export default DashboardPaciente;
