import React, { useState } from 'react';
import { Form, Button } from 'react-bootstrap';

function MarcarConsulta() {
    const [medico, setMedico] = useState('');
    const [data, setData] = useState('');
    const [hora, setHora] = useState('');
    const [motivo, setMotivo] = useState('');

    const handleSubmit = (e) => {
        e.preventDefault();
        // Aqui você pode fazer a chamada para agendar a consulta
        console.log("Consulta marcada:", { medico, data, hora, motivo });
    };

    return (
        <div>
            <h3>Marcar Consulta</h3>
            <Form onSubmit={handleSubmit}>
                <Form.Group controlId="formMedico">
                    <Form.Label>Médico</Form.Label>
                    <Form.Control 
                        type="text" 
                        placeholder="Nome do Médico" 
                        value={medico}
                        onChange={(e) => setMedico(e.target.value)} 
                    />
                </Form.Group>

                <Form.Group controlId="formData">
                    <Form.Label>Data</Form.Label>
                    <Form.Control 
                        type="date" 
                        value={data}
                        onChange={(e) => setData(e.target.value)} 
                    />
                </Form.Group>

                <Form.Group controlId="formHora">
                    <Form.Label>Hora</Form.Label>
                    <Form.Control 
                        type="time" 
                        value={hora}
                        onChange={(e) => setHora(e.target.value)} 
                    />
                </Form.Group>

                <Form.Group controlId="formMotivo">
                    <Form.Label>Motivo da Consulta</Form.Label>
                    <Form.Control 
                        type="text" 
                        placeholder="Descreva o motivo" 
                        value={motivo}
                        onChange={(e) => setMotivo(e.target.value)} 
                    />
                </Form.Group>

                <Button variant="primary" type="submit">
                    Agendar Consulta
                </Button>
            </Form>
        </div>
    );
}

export default MarcarConsulta;
