// src/pages/LoginPaciente.js

import React, { useState } from 'react';
import { Container, Button, Form, Alert } from 'react-bootstrap';
import axios from 'axios';
import '../styles/LoginPaciente.css';

const LoginPaciente = () => {
  const [isLogin, setIsLogin] = useState(true); // Alterna entre Login e Cadastro
  const [formData, setFormData] = useState({
    name: '',
    cpf: '',
    address: '', // Adicionando o campo de endereço
    birthDate: {
      year: '',
      month: '',
      day: ''
    },
    email: '',
    password: ''
  });
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');

  const handleInputChange = (e) => {
    const { id, value } = e.target;
    setFormData((prevData) => ({
      ...prevData,
      [id]: value
    }));
  };

  const handleDateChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevData) => ({
      ...prevData,
      birthDate: {
        ...prevData.birthDate,
        [name]: value
      }
    }));
  };

  const handleSignup = async (e) => {
    e.preventDefault();
    setError('');
    setSuccess('');

    // Formata a data como "YYYY-MM-DD"
    const formattedBirthDate = `${formData.birthDate.year}-${formData.birthDate.month}-${formData.birthDate.day}`;

    try {
      const response = await axios.post('http://localhost:8080/paciente/crud/addPaciente', {
        name: formData.name,
        cpf: formData.cpf,
        address: formData.address, // Enviando o endereço
        birthDate: formattedBirthDate,
        email: formData.email,
      });
      setSuccess('Conta criada com sucesso!');
      setFormData({ name: '', cpf: '', address: '', birthDate: { year: '', month: '', day: '' }, email: '', password: '' }); // Limpa o formulário
    } catch (error) {
      setError('Erro ao criar conta. Verifique os dados e tente novamente.');
    }
  };

  return (
    <Container className="d-flex align-items-center justify-content-center vh-100">
      <div className={`container ${isLogin ? '' : 'active'}`} id="container">
        {/* Formulário de Cadastro */}
        <div className="form-container sign-up">
          <Form onSubmit={handleSignup}>
            <h1>Criar Conta</h1>
            {error && <Alert variant="danger">{error}</Alert>}
            {success && <Alert variant="success">{success}</Alert>}
            <Form.Group controlId="name">
              <Form.Control
                type="text"
                placeholder="Nome"
                value={formData.name}
                onChange={handleInputChange}
              />
            </Form.Group>
            <Form.Group controlId="cpf">
              <Form.Control
                type="text"
                placeholder="CPF"
                value={formData.cpf}
                onChange={handleInputChange}
              />
            </Form.Group>
            <Form.Group controlId="address">
              <Form.Control
                type="text"
                placeholder="Endereço"
                value={formData.address}
                onChange={handleInputChange}
              />
            </Form.Group>
            <Form.Group controlId="birthDate">
              <div className="d-flex">
                <Form.Control
                  as="select"
                  name="year"
                  value={formData.birthDate.year}
                  onChange={handleDateChange}
                  required
                >
                  <option value="">Ano</option>
                  {Array.from({ length: 100 }, (_, i) => (
                    <option key={i} value={2023 - i}>
                      {2023 - i}
                    </option>
                  ))}
                </Form.Control>

                <Form.Control
                  as="select"
                  name="month"
                  value={formData.birthDate.month}
                  onChange={handleDateChange}
                  required
                >
                  <option value="">Mês</option>
                  {Array.from({ length: 12 }, (_, i) => (
                    <option key={i} value={String(i + 1).padStart(2, '0')}>
                      {i + 1}
                    </option>
                  ))}
                </Form.Control>

                <Form.Control
                  as="select"
                  name="day"
                  value={formData.birthDate.day}
                  onChange={handleDateChange}
                  required
                >
                  <option value="">Dia</option>
                  {Array.from({ length: 31 }, (_, i) => (
                    <option key={i} value={String(i + 1).padStart(2, '0')}>
                      {i + 1}
                    </option>
                  ))}
                </Form.Control>
              </div>
            </Form.Group>
            <Form.Group controlId="email">
              <Form.Control
                type="email"
                placeholder="Email"
                value={formData.email}
                onChange={handleInputChange}
              />
            </Form.Group>
            <Form.Group controlId="password">
              <Form.Control
                type="password"
                placeholder="Senha"
                value={formData.password}
                onChange={handleInputChange}
              />
            </Form.Group>
            <Button variant="primary" type="submit">
              Criar Conta
            </Button>
          </Form>
        </div>

        {/* Formulário de Login */}
        <div className="form-container sign-in">
          <Form>
            <h1>Entrar</h1>
            <Form.Group controlId="formCpf">
              <Form.Control type="text" placeholder="CPF" />
            </Form.Group>
            <Form.Group controlId="formSenha">
              <Form.Control type="password" placeholder="Senha" />
            </Form.Group>
            <Button variant="primary" type="submit">
              Entrar
            </Button>
          </Form>
          <p>
            Não possui uma conta?{' '}
            <Button variant="link" onClick={() => setIsLogin(false)}>
              Cadastre-se
            </Button>
          </p>
        </div>

        {/* Toggle */}
        <div className="toggle-container">
          <div className="toggle">
            <div className="toggle-panel toggle-left">
              <h1>Bem-vindo de Volta!</h1>
              <p>Insira seus dados pessoais para acessar todas as funcionalidades.</p>
              <Button className="hidden" onClick={() => setIsLogin(true)}>
                Entrar
              </Button>
            </div>
            <div className="toggle-panel toggle-right">
              <h1>Olá, Paciente!</h1>
              <p>
                Cadastre-se com seus dados pessoais para acessar todas as funcionalidades do
                Health Horizon
              </p>
              <Button className="hidden" onClick={() => setIsLogin(false)}>
                Criar Conta
              </Button>
            </div>
          </div>
        </div>
      </div>
    </Container>
  );
};

export default LoginPaciente;
