import React, { useState } from 'react';
import { Container, Row, Col, ListGroup } from 'react-bootstrap';
import MarcarConsulta from './MarcarConsulta'; // Importa o novo componente
import '../styles/DashboardPaciente.css';

function DashboardPaciente() {
    const [currentSection, setCurrentSection] = useState('home'); // Seção atual

    const renderContent = () => {
        switch (currentSection) {
            case 'marcar-consulta':
                return <MarcarConsulta />;
            case 'ver-exames':
                return <div>Conteúdo para Ver Exames</div>; // Placeholder
            case 'historico-consultas':
                return <div>Conteúdo para Histórico de Consultas</div>; // Placeholder
            case 'perfil':
                return <div>Conteúdo para Perfil</div>; // Placeholder
            default:
                return (
                    <div>
                        <h2>Bem-vindo à Dashboard do Paciente</h2>
                        <p>Escolha uma opção no menu para começar.</p>
                    </div>
                );
        }
    };

    return (
        <Container fluid className="dashboard-paciente">
            <Row>
                <Col md={3} className="sidebar">
                    <h4>Menu do Paciente</h4>
                    <ListGroup variant="flush">
                        <ListGroup.Item action onClick={() => setCurrentSection('marcar-consulta')}>
                            Marcar Consulta
                        </ListGroup.Item>
                        <ListGroup.Item action onClick={() => setCurrentSection('ver-exames')}>
                            Ver Exames
                        </ListGroup.Item>
                        <ListGroup.Item action onClick={() => setCurrentSection('historico-consultas')}>
                            Histórico de Consultas
                        </ListGroup.Item>
                        <ListGroup.Item action onClick={() => setCurrentSection('perfil')}>
                            Perfil
                        </ListGroup.Item>
                    </ListGroup>
                </Col>

                <Col md={9} className="content">
                    {renderContent()} {/* Renderiza o conteúdo com base na seção atual */}
                </Col>
            </Row>
        </Container>
    );
}

export default DashboardPaciente;
