// src/pages/DashboardMedico.js

import React, { useState, useEffect } from 'react';
import { Container, Row, Col, ListGroup } from 'react-bootstrap';
import ConsultasAgendadasMedico from './ConsultasAgendadasMedico';
import CadastrarExame from './CadastrarExame';
import AdicionarHorarios from './AdicionarHorarios';
import '../styles/DashboardMedico.css';

function DashboardMedico() {
    const [currentSection, setCurrentSection] = useState('consultas-agendadas');
    const [medicoNome, setMedicoNome] = useState('');

    useEffect(() => {
        // Pega o nome do médico do localStorage
        const nome = localStorage.getItem('medicoNome');
        if (nome) setMedicoNome(nome);
    }, []);

    const renderContent = () => {
        switch (currentSection) {
            case 'consultas-agendadas':
                return <ConsultasAgendadasMedico />;
            case 'cadastrar-exame':
                return <CadastrarExame />;
            case 'adicionar-horarios':
                return <AdicionarHorarios />;
            default:
                return <p>Selecione uma opção para começar.</p>;
        }
    };

    return (
        <Container fluid className="dashboard-medico vh-100">
            <Row className="h-100">
                <Col md={3} className="sidebar">
                    <h4>Menu do Médico</h4>
                    <ListGroup variant="flush">
                        <ListGroup.Item action onClick={() => setCurrentSection('consultas-agendadas')}>
                            Consultas Agendadas
                        </ListGroup.Item>
                        <ListGroup.Item action onClick={() => setCurrentSection('cadastrar-exame')}>
                            Cadastrar Exame
                        </ListGroup.Item>
                        <ListGroup.Item action onClick={() => setCurrentSection('adicionar-horarios')}>
                            Adicionar Horários
                        </ListGroup.Item>
                        <ListGroup.Item action href="#perfil">Perfil</ListGroup.Item>
                    </ListGroup>
                </Col>

                <Col md={9} className="content d-flex flex-column h-100">
                    <h2>Bem-vindo, Dr. {medicoNome}</h2>
                    <p>Selecione uma opção para começar.</p>
                    <div id="conteudo-dinamico" className="flex-grow-1">
                        {renderContent()}
                    </div>
                </Col>
            </Row>
        </Container>
    );
}

export default DashboardMedico;
