// src/pages/DashboardMedico.js

import React, { useState } from 'react';
import { Container, Row, Col, ListGroup } from 'react-bootstrap';
import ConsultasAgendadasMedico from './ConsultasAgendadasMedico'; // Importe o componente de consultas agendadas
import CadastrarExame from './CadastrarExame'; // Supondo que você também tenha esse componente
import AdicionarHorarios from './AdicionarHorarios'; // Supondo que você também tenha esse componente
import '../styles/DashboardMedico.css';

function DashboardMedico() {
    const [currentSection, setCurrentSection] = useState('consultas-agendadas'); // Estado para controlar a seção atual

    const renderContent = () => {
        switch (currentSection) {
            case 'consultas-agendadas':
                return <ConsultasAgendadasMedico />;
            case 'cadastrar-exame':
                return <CadastrarExame />;
            case 'adicionar-horarios':
                return <AdicionarHorarios />;
            default:
                return <p>Selecione uma opção para começar.</p>;
        }
    };

    return (
        <Container fluid className="dashboard-medico">
            <Row>
                {/* Sidebar */}
                <Col md={3} className="sidebar">
                    <h4>Menu do Médico</h4>
                    <ListGroup variant="flush">
                        <ListGroup.Item action onClick={() => setCurrentSection('consultas-agendadas')}>
                            Consultas Agendadas
                        </ListGroup.Item>
                        <ListGroup.Item action onClick={() => setCurrentSection('cadastrar-exame')}>
                            Cadastrar Exame
                        </ListGroup.Item>
                        <ListGroup.Item action onClick={() => setCurrentSection('adicionar-horarios')}>
                            Adicionar Horários
                        </ListGroup.Item>
                        <ListGroup.Item action href="#perfil">Perfil</ListGroup.Item>
                    </ListGroup>
                </Col>

                {/* Conteúdo Principal */}
                <Col md={9} className="content">
                    <h2>Bem-vindo, Dr. [Nome do Médico]</h2>
                    <p>Selecione uma opção para começar.</p>
                    {/* Área de Conteúdo Dinâmico */}
                    <div id="conteudo-dinamico">
                        {renderContent()} {/* Renderiza o conteúdo com base na seção selecionada */}
                    </div>
                </Col>
            </Row>
        </Container>
    );
}

export default DashboardMedico;
