// src/pages/MarcarConsulta.js

import React, { useState, useEffect } from 'react';
import { Form, Button, Alert } from 'react-bootstrap';

function MarcarConsulta() {
  const [medico, setMedico] = useState('');
  const [data, setData] = useState('');
  const [hora, setHora] = useState('');
  const [motivo, setMotivo] = useState('');
  const [errorMessage, setErrorMessage] = useState('');

  useEffect(() => {
    
    const medicoData = JSON.parse(localStorage.getItem('medico'));
    if (medicoData) {
      setMedico(medicoData.nome); 
    }
  }, []);

  const handleSubmit = (e) => {
    e.preventDefault();

    if (!data || !hora || !motivo) {
      setErrorMessage("Por favor, preencha todos os campos.");
      return;
    }

    // Salvar a consulta (no localStorage como exemplo)
    const consultaData = {
      medico,
      data,
      hora,
      motivo,
    };
    const consultas = JSON.parse(localStorage.getItem('consultas')) || [];
    consultas.push(consultaData);
    localStorage.setItem('consultas', JSON.stringify(consultas));

    console.log("Consulta marcada:", consultaData);
  };

  return (
    <div>
      <h3>Marcar Consulta com Dr(a). {medico}</h3>
      {errorMessage && <Alert variant="danger">{errorMessage}</Alert>}
      <Form onSubmit={handleSubmit}>
        <Form.Group controlId="formData">
          <Form.Label>Data</Form.Label>
          <Form.Control 
            type="date" 
            value={data}
            onChange={(e) => setData(e.target.value)} 
          />
        </Form.Group>

        <Form.Group controlId="formHora">
          <Form.Label>Hora</Form.Label>
          <Form.Control 
            type="time" 
            value={hora}
            onChange={(e) => setHora(e.target.value)} 
          />
        </Form.Group>

        <Form.Group controlId="formMotivo">
          <Form.Label>Motivo da Consulta</Form.Label>
          <Form.Control 
            type="text" 
            placeholder="Descreva o motivo" 
            value={motivo}
            onChange={(e) => setMotivo(e.target.value)} 
          />
        </Form.Group>

        <Button variant="primary" type="submit">
          Agendar Consulta
        </Button>
      </Form>
    </div>
  );
}

export default MarcarConsulta;
