// src/pages/LoginMedico.js

import React, { useState } from 'react';
import { Container, Button, Form, Alert } from 'react-bootstrap';
import { useNavigate } from 'react-router-dom';
import '../styles/LoginMedico.css';

const LoginMedico = () => {
  const [isLogin, setIsLogin] = useState(true);
  const [formData, setFormData] = useState({
    name: '',
    crm: '',
    especialidade: '',
    contact: '',
  });
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const navigate = useNavigate();

  const handleInputChange = (e) => {
    const { id, value } = e.target;
    setFormData((prevData) => ({
      ...prevData,
      [id]: value
    }));
  };

  const handleSignup = (e) => {
    e.preventDefault();
    setError('');
    setSuccess('Conta criada com sucesso!');
    setFormData({ name: '', crm: '', especialidade: '', contact: '', password: '' });
    
    // Redireciona para a Dashboard com o nome do médico
    navigate('/dashboard-medico', { state: { name: formData.name } });
  };

  return (
    <Container className="d-flex align-items-center justify-content-center vh-100">
      <div className={`container ${isLogin ? '' : 'active'}`} id="container">
        <div className="form-container sign-up">
          <Form onSubmit={handleSignup}>
            <h1>Criar Conta</h1>
            {error && <Alert variant="danger">{error}</Alert>}
            {success && <Alert variant="success">{success}</Alert>}
            <Form.Group controlId="name">
              <Form.Control
                type="text"
                placeholder="Nome"
                value={formData.name}
                onChange={handleInputChange}
                required
              />
            </Form.Group>
            <Form.Group controlId="crm">
              <Form.Control
                type="text"
                placeholder="CRM (ex: 123456/CE)"
                pattern="^[0-9]{6}/[A-Za-z]{2}$"
                title="CRM deve estar no formato '123456/CE'"
                value={formData.crm}
                onChange={handleInputChange}
                required
              />
            </Form.Group>
            <Form.Group controlId="especialidade">
              <Form.Control
                type="text"
                placeholder="Especialidade"
                value={formData.especialidade}
                onChange={handleInputChange}
                required
              />
            </Form.Group>
            <Form.Group controlId="contact">
              <Form.Control
                type="tel"
                placeholder="Telefone"
                value={formData.contact}
                onChange={handleInputChange}
                required
              />
            </Form.Group>
            <Button variant="primary" type="submit">
              Criar Conta
            </Button>
          </Form>
        </div>
        
        {/* Restante do código... */}
      </div>
    </Container>
  );
};

export default LoginMedico;
