// src/pages/AdminDashboard.js

import React, { useState } from 'react';
import { Container, Row, Col, ListGroup } from 'react-bootstrap';
import CadastroMedico from './LoginMedico';
import CadastroPaciente from './LoginPaciente';
import MarcarConsultaPaciente from './MarcarConsulta';
import MedicosDisponiveis from './MedicosDisponiveis';

function AdminDashboard() {
    const [currentSection, setCurrentSection] = useState('home');

    const renderContent = () => {
        switch (currentSection) {
            case 'cadastro-medico':
                return <CadastroMedico />;
            case 'cadastro-paciente':
                return <CadastroPaciente />;
            case 'marcar-consulta-paciente':
                return <MarcarConsultaPaciente />;
            case 'medicos-disponiveis':
                return <MedicosDisponiveis />;
            default:
                return (
                    <div>
                        <h2>Bem-vindo à Dashboard do Admin</h2>
                        <p>Escolha uma opção no menu para começar.</p>
                    </div>
                );
        }
    };

    return (
        <Container fluid className="admin-dashboard">
            <Row>
                <Col md={3} className="sidebar">
                    <h4>Menu do Admin</h4>
                    <ListGroup variant="flush">
                        <ListGroup.Item action onClick={() => setCurrentSection('cadastro-medico')}>
                            Cadastro de Médico
                        </ListGroup.Item>
                        <ListGroup.Item action onClick={() => setCurrentSection('cadastro-paciente')}>
                            Cadastro de Paciente
                        </ListGroup.Item>
                        <ListGroup.Item action onClick={() => setCurrentSection('marcar-consulta-paciente')}>
                            Marcar Consulta do Paciente
                        </ListGroup.Item>
                        <ListGroup.Item action onClick={() => setCurrentSection('medicos-disponiveis')}>
                            Médicos Disponíveis
                        </ListGroup.Item>
                    </ListGroup>
                </Col>

                <Col md={9} className="content">
                    {renderContent()}
                </Col>
            </Row>
        </Container>
    );
}

export default AdminDashboard;
