import React, { useState, useEffect } from 'react';
import { Table, Spinner } from 'react-bootstrap';

function VerExames() {
    const [exames, setExames] = useState([]);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        // Simulação de uma chamada de API para buscar os exames do paciente
        const fetchExames = async () => {
            try {
                // Aqui você pode substituir pela chamada real à sua API
                const response = await fetch('/api/exames'); // Exemplo de URL
                const data = await response.json();
                setExames(data);
            } catch (error) {
                console.error("Erro ao buscar exames:", error);
            } finally {
                setLoading(false);
            }
        };

        fetchExames();
    }, []);

    if (loading) {
        return <Spinner animation="border" />;
    }

    return (
        <div>
            <h3>Meus Exames</h3>
            {exames.length === 0 ? (
                <p>Você ainda não tem exames cadastrados.</p>
            ) : (
                <Table striped bordered hover>
                    <thead>
                        <tr>
                            <th>Nome do Exame</th>
                            <th>Data de Realização</th>
                            <th>Resultado</th>
                        </tr>
                    </thead>
                    <tbody>
                        {exames.map((exame, index) => (
                            <tr key={index}>
                                <td>{exame.nome}</td>
                                <td>{exame.dataRealizacao}</td>
                                <td>{exame.resultado}</td>
                            </tr>
                        ))}
                    </tbody>
                </Table>
            )}
        </div>
    );
}

export default VerExames;
