import React, { useState } from 'react';
import { Container, Button, Form } from 'react-bootstrap';
import medicoService from '../services/medicoService'; // Importa o serviço
import '../styles/CadastroMedico.css'; // Importa o CSS específico

const CadastroMedico = () => {
  const [medico, setMedico] = useState({
    name: '',
    especialidade: '',
    crm: '',
    contact: '',
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setMedico((prevState) => ({
      ...prevState,
      [name]: value,
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await medicoService.addMedico(medico);
      console.log('Médico cadastrado com sucesso:', response);
      // Redirecionar ou mostrar mensagem de sucesso
    } catch (error) {
      console.error('Erro ao cadastrar médico:', error.message);
    }
  };

  return (
    <Container className="d-flex align-items-center justify-content-center vh-100">
      <Form onSubmit={handleSubmit}>
        <h1>Cadastrar Médico</h1>
        <Form.Group controlId="formNome">
          <Form.Control
            type="text"
            placeholder="Nome"
            name="name"
            value={medico.name}
            onChange={handleChange}
            required
          />
        </Form.Group>
        <Form.Group controlId="formEspecialidade">
          <Form.Control
            type="text"
            placeholder="Especialidade"
            name="especialidade"
            value={medico.especialidade}
            onChange={handleChange}
            required
          />
        </Form.Group>
        <Form.Group controlId="formCrm">
          <Form.Control
            type="text"
            placeholder="CRM (ex: 123456/CE)"
            name="crm"
            value={medico.crm}
            onChange={handleChange}
            pattern="^[0-9]{6}/[A-Za-z]{2}$"
            title="CRM"
            required
          />
        </Form.Group>
        <Form.Group controlId="formTelefone">
          <Form.Control
            type="tel"
            placeholder="Telefone"
            name="contact"
            value={medico.contact}
            onChange={handleChange}
            required
          />
        </Form.Group>
        <Button variant="primary" type="submit">
          Criar Conta
        </Button>
      </Form>
    </Container>
  );
};

export default CadastroMedico;
