// src/pages/DashboardMedico.js

import React, { useEffect, useState } from 'react';
import { Container, Row, Col, ListGroup, Table, Spinner } from 'react-bootstrap';
import '../styles/DashboardMedico.css';

function DashboardMedico() {
  const [nomeMedico, setNomeMedico] = useState('');
  const [consultas, setConsultas] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // Recupera o nome do médico do localStorage
    const medicoData = JSON.parse(localStorage.getItem('medico'));
    if (medicoData) {
      setNomeMedico(medicoData.nome);
    }

    // Recupera as consultas do localStorage
    const consultasData = JSON.parse(localStorage.getItem('consultas')) || [];
    setConsultas(consultasData);
    setLoading(false);
  }, []);

  if (loading) {
    return <Spinner animation="border" />;
  }

  return (
    <Container fluid className="dashboard-medico vh-100">
      <Row className="h-100">
        <Col md={3} className="sidebar">
          <h4>Menu do Médico</h4>
          <ListGroup variant="flush">
            <ListGroup.Item action>
              Ver Consultas
            </ListGroup.Item>
            <ListGroup.Item action>
              Ver Pacientes
            </ListGroup.Item>
            <ListGroup.Item action>
              Perfil
            </ListGroup.Item>
          </ListGroup>
        </Col>
        <Col md={9} className="content d-flex flex-column h-100">
          <div>
            <h2>Bem-vindo, Dr. {nomeMedico}!</h2>
            <h3>Consultas Agendadas</h3>
            {consultas.length === 0 ? (
              <p>Não há consultas agendadas.</p>
            ) : (
              <Table striped bordered hover>
                <thead>
                  <tr>
                    <th>Data</th>
                    <th>Hora</th>
                    <th>Paciente</th>
                    <th>Motivo</th>
                  </tr>
                </thead>
                <tbody>
                  {consultas.map((consulta, index) => (
                    <tr key={index}>
                      <td>{consulta.data}</td>
                      <td>{consulta.hora}</td>
                      <td>{consulta.medico}</td> {/* Aqui você pode substituir pelo nome do paciente, se quiser */}
                      <td>{consulta.motivo}</td>
                    </tr>
                  ))}
                </tbody>
              </Table>
            )}
          </div>
        </Col>
      </Row>
    </Container>
  );
}

export default DashboardMedico;
