// src/pages/CadastroPaciente.js

import React, { useState } from 'react';
import { Container, Button, Form } from 'react-bootstrap';
import { useNavigate } from 'react-router-dom'; // Importa o hook para navegação

const CadastroPaciente = () => {
  const [pacienteData, setPacienteData] = useState({
    name: '',
    cpf: '',
    dataNascimento: '',
    telefone: '',
  });

  const navigate = useNavigate();

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setPacienteData({ ...pacienteData, [name]: value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    console.log('Dados do paciente:', pacienteData);
    try {
      const novoPaciente = await cadastrarPaciente(pacienteData);
      console.log('Paciente cadastrado com sucesso:', novoPaciente);

      // Salva os dados do paciente no localStorage
      localStorage.setItem('paciente', JSON.stringify(pacienteData));

      // Redireciona o paciente para a Dashboard
      navigate('/dashboard-paciente');
    } catch (error) {
      console.error('Erro ao cadastrar paciente:', error);
      alert('Erro ao cadastrar paciente: ' + error.message);
    }
  };

  return (
    <Container>
      <h1>Cadastrar Paciente</h1>
      <Form onSubmit={handleSubmit}>
        <Form.Group controlId="formNome">
          <Form.Control
            type="text"
            name="name"
            placeholder="Nome"
            value={pacienteData.name}
            onChange={handleInputChange}
            required
          />
        </Form.Group>
        <Form.Group controlId="formCPF">
          <Form.Control
            type="text"
            name="cpf"
            placeholder="CPF"
            value={pacienteData.cpf}
            onChange={handleInputChange}
            required
          />
        </Form.Group>
        <Form.Group controlId="formDataNascimento">
          <Form.Control
            type="date"
            name="dataNascimento"
            placeholder="Data de Nascimento"
            value={pacienteData.dataNascimento}
            onChange={handleInputChange}
            required
          />
        </Form.Group>
        <Form.Group controlId="formTelefone">
          <Form.Control
            type="tel"
            name="telefone"
            placeholder="Telefone"
            value={pacienteData.telefone}
            onChange={handleInputChange}
            required
          />
        </Form.Group>
        <Button variant="primary" type="submit">
          Criar Conta
        </Button>
      </Form>
    </Container>
  );
};

export default CadastroPaciente;
